mapper.select:
SELECT

FROM PERSON_INFO
WHERE PERSON_INFO . ID = #{id,jdbcType=DECIMAL}

mapper.sql:
WHERE PERSON_INFO . SALES_DEPARTMENT_ID = COM_ORGANIZATION . ID (+)


		and COM_ORGANIZATION.ORG_CODE LIKE #{comOrganization.orgCode} ||'%'




		AND PERSON_INFO . ID = PERSON_CONTACT_INFO . PERSON_ID

			AND PERSON_CONTACT_INFO . NAME = #{personContactInfo.name}



			AND (PERSON_CONTACT_INFO . MPHONE = #{personContactInfo.mphone} OR
			PERSON_CONTACT_INFO . TEL = #{personContactInfo.mphone}
			OR
			PERSON_CONTACT_INFO . CTEL = #{personContactInfo.mphone})




	AND PERSON_INFO . NAME LIKE '' || #{name} || '%'



	AND (PERSON_INFO . MPHONE = #{mphone} OR PERSON_INFO . TEL =
	#{mphone}
	OR PERSON_INFO . C_TEL = #{mphone})


	AND PERSON_INFO . IDNUM = #{idnum}


	AND PERSON_INFO . REAL_ESTATE_LICENSE_CODE =
	#{realEstateLicenseCode}

mapper.select:
SELECT

FROM PERSON_INFO
WHERE 1=1

	AND trim(IDNUM) = #{idNum}


	AND trim(name) = #{borrowerName}
